
///Funciones para la pagina index de los folios
function mostrarRegistro() {
  document.getElementById('registro').style.display = 'block';
  document.getElementById('consultarf').style.display = 'block';
  document.getElementById('registro01').style.display = 'block';
  document.getElementById('buscar').style.display = 'none';
}
function mostrarbuscar() {
  document.getElementById('registro01').style.display = 'none';
  document.getElementById('buscar').style.display = 'block';
  document.getElementById('consultarf').style.display = 'none';
}
